const words=[{word:"blacklist",suggestions:["blocklist","denylist"]},{word:"handicapped",suggestions:["impeded"]},{word:"guys",suggestions:["all","team","folks","everyone"]},{word:"girls",suggestions:["all","team","folks","everyone"]},{word:"whitelist",suggestions:["allowlist","passlist"]},{word:"master",suggestions:["primary","main","leader","original"]},{word:"slave",suggestions:["secondary","replica"]}];function getRandom(){const t=original.innerHTML.trim().toLowerCase(),e=words.filter(e=>e.word!==t),n=e[Math.floor(Math.random()*e.length)];original.innerHTML=n.word,getAlternative()}function getAlternative(){const o=original.innerHTML.toLowerCase(),e=words.find(e=>e.word===o).suggestions;if(e.length===1){const[t]=e;alternatives.innerHTML=t,btnGetAlternative.innerHTML="No suggested alternatives",btnGetAlternative.setAttribute("disabled",!0);return}btnGetAlternative.innerHTML="Get alternative",btnGetAlternative.removeAttribute("disabled");const t=alternatives.innerHTML.trim(),n=e.filter(e=>e!==t),s=n[Math.floor(Math.random()*n.length)];console.log({currentSuggestion:t,chosenSuggestion:s}),alternatives.innerHTML=s}btnGetAlternative.onclick=getAlternative,btnRandom.onclick=getRandom